mapper.select:
SELECT a.idOrder, a.idUser, a.imOrdine, a.idDeliveryCost, a.idTransaction, a.idAddressFat, a.idAddressSpe, a.idTrackingNumber, b.idProdotto, b.pgArticle, b.qtArticle, c.idStato, c.dtStato, c.txNote, d.txStato, f.IDAddress as idAddressBilling, f.txNome as txNomeBilling,f.txCognome as txCognomeBilling, f.txVia as txViaBilling, f.txComune as txComuneBilling, f.cdCap as cdCapBilling, f.txProvincia as txProvinciaBilling, f.txNazione as txNazioneBilling, f.txTel as txTelBilling, f.txEtichetta as etichettaBilling, f.fgDeleted, g.IDAddress as idAddressDelivery, g.txNome as txNomeDelivery, g.txCognome as txCognomeDelivery, g.txVia as txViaDelivery, g.txComune as txComuneDelivery, g.cdCap as cdCapDelivery, g.txProvincia as txProvinciaDelivery, g.txNazione as txNazioneDelivery, g.txTel as txTelDelivery,g.txEtichetta as etichettaDelivery, g.fgDeleted, h.txNome, h.txCognome, i.idDeliveryCost as deliveryCost, i.txDescrizione as desSpeseSpedizione, i.imSpese as speseSpedizione, i.txDescrizioneExt, i.txLinkTracking FROM ${database.schema}orders a, ${database.schema}orders_articles b, ${database.schema}states_orders c, ${database.schema}states d, ${database.schema}addresses f, ${database.schema}addresses g, ${database.schema}customer h, ${database.schema}delivery_cost i WHERE a.idOrder = b.idOrder and a.idOrder = c.idOrder
and a.idOrder = #{idOrder}
and a.idUser = #{idUser}
and a.idAddressFat = f.idAddress and a.idAddressSpe = g.idAddress and c.idStato = d.idStato
and c.idStato = #{idStato}
and c.dtStato = (SELECT MAX(e.dtStato) FROM ${database.schema}states_orders e WHERE e.idOrder = a.idOrder and c.idStato != 1)
and a.idUser = h.idUser and i.idDeliveryCost = a.idDeliveryCost

mapper.update:
		UPDATE ${database.schema}orders
		
			SET idTransaction = #{idTransaction}
		
		
			SET idTrackingNumber = #{idTrackingNumber}
		
		WHERE idOrder = #{idOrder}
